import React, {Component} from "react";
import Register from "./Register/Register";
import Login from "./Login/Login";
import {Grid, Snackbar, SnackbarContent} from "@material-ui/core";

class DynamicUserForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            snackOpened: false,
            snackMessage: null
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSnackClose = this.handleSnackClose.bind(this);
    }

    handleSubmit(event, data) {
        event.preventDefault();
        const action = this.props.registerForm ? 'signup' : 'singin';

        fetch(`http://localhost:9999/auth/${action}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then(response => response.json())
            .then(body => {
                if (body.errors.length > 0) {
                    this.setState({
                        snackOpened: true,
                        snackMessage: this.props.registerForm ? 'Registration Failed' : 'Login Failed'
                    })
                }
            });
    }

    handleSnackClose() {
        this.setState({snackOpened: !this.state.snackOpened})
    }

    render() {
        return (
            <Grid
                container
                spacing={0}
                // alignItems="center"
                justify="center"
            >
                <Grid container spacing={0} justify="center">
                    <Grid item xs={10}>
                        {
                            this.props.registerForm
                                ? (<Register handleSubmit={this.handleSubmit}/>)
                                : (<Login/>)
                        }
                    </Grid>
                </Grid>
                {/*<Snackbar*/}
                    {/*anchorOrigin={{*/}
                        {/*vertical: 'top',*/}
                        {/*horizontal: 'right'*/}
                    {/*}}*/}
                    {/*aria-describedby="client-snackbar"*/}
                    {/*open={this.state.snackOpened}*/}
                    {/*autoHideDuration={5000}*/}
                    {/*onClose={this.handleSnackClose}*/}
                    {/*ContentProps={{*/}
                        {/*'aria-describedby': 'message-id',*/}
                    {/*}}*/}
                    {/*message={<span id="message-id">{this.state.snackMessage}</span>}*/}
                {/*/>*/}
                <SnackbarContent
                    aria-describedby="client-snackbar"
                    message={
                        <span id="client-snackbar" >
                            {this.state.snackMessage}
        </span>
                    }
                />
            </Grid>

        )
    }
}

export default DynamicUserForm;
