import React, {Component} from "react";
import {Drawer, List, ListItem, ListItemIcon, ListItemText, Divider, Collapse} from "@material-ui/core";
import {HomeTwoTone, StoreTwoTone} from "@material-ui/icons";
import {NavLink} from "react-router-dom";
import './SideBar.css';

class SideBar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            flavourCollapseOpen: true,
        }
    }

    render() {
        const {open, onClose} = this.props;
        return (
            <Drawer
                variant="persistent"
                open={open}
                onClose={onClose}
            >
                <List component="nav">
                    <NavLink to={"/"}>
                        <ListItem button key={"Home"}>
                            <ListItemIcon><HomeTwoTone/></ListItemIcon>
                            <ListItemText primary={"Home"}/>
                        </ListItem>
                    </NavLink>
                    <Collapse in={this.state.flavourCollapseOpen} timeout="auto" unmountOnExit>
                        <List component="div" disablePadding>
                            <NavLink to={"/view/flavour"}>
                                <ListItem button key={"ViewFlavours"}>
                                    <ListItemIcon><StoreTwoTone/></ListItemIcon>
                                    <ListItemText primary={"ViewFlavours"}/>
                                </ListItem>
                            </NavLink>
                            <NavLink to={"/add/flavour"}>
                                <ListItem button key={"AddFlavours"}>
                                    <ListItemIcon><StoreTwoTone/></ListItemIcon>
                                    <ListItemText primary={"AddFlavours"}/>
                                </ListItem>
                            </NavLink>
                        </List>
                    </Collapse>
                </List>
                <Divider/>
                <List>
                    {/*{['All mail', 'Trash', 'Spam'].map((text, index) => (*/}
                    {/*<ListItem button key={text}>*/}
                    {/*<ListItemIcon>{index % 2 === 0 ? <InboxIcon/> : <MailIcon/>}</ListItemIcon>*/}
                    {/*<ListItemText primary={text}/>*/}
                    {/*</ListItem>*/}
                    {/*))}*/}
                </List>
            </Drawer>
        )
    }
}

export default SideBar;
