import React, {Component, Fragment} from 'react';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import {Snackbar} from '@material-ui/core';
import DynamicUserForm from "./components/DynamicUserForm/DynamicUserForm";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: null,
            isAdmin: false,
            isModerator: false,
            snackOpened: false,
            snackMessage: null,
        }
    }

    registerUser(e, data) {
        e.preventDefault();
        fetch('http://localhost:9999/auth/signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then(response => response.json())
            .then(body => {
                if (body.errors.length > 0) {
                    console.log(body.errors);
                    this.setState({snackOpened: true, snackMessage: 'Register failed.'})
                }
            });
    }

    render() {
        return (
            <BrowserRouter>
                <Fragment>
                    <Navbar/>

                    <Switch>
                        {/*<Route path="/" exact component={Home}/>*/}
                        <Route path="/register" render={() => <DynamicUserForm register={true} />}/>
                        <Route path="/login" render={() => <DynamicUserForm register={false} />}/>

                    </Switch>
                </Fragment>
            </BrowserRouter>
        );
    }
}

export default App;
