import React, {Component, Fragment} from 'react';
import './App.css';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import NavBar from './components/NavBar/NavBar';
import DynamicUserForm from "./components/DynamicUserForm/DynamicUserForm";
import SnackbarWrapper from "./components/SnackbarWrapper/SnackbarWrapper";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isAdmin: false,
            isModerator: false,
            snackOpened: false,
            snackType: null,
            snackMessage: null,
        };

        this.loginUser = this.loginUser.bind(this);
        this.handleSnackClose = this.handleSnackClose.bind(this);
    }

    loginUser(event, user) {
        event.preventDefault();
        fetch('http://localhost:9999/auth/signin', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(user)
        })
            .then(res => res.json())
            .then((data) => {
                if (!data.email) {
                    this.setState({
                        snackOpened: true,
                        snackType: 'error',
                        snackMessage: 'Login failed!',
                    });
                } else {
                    this.setState({
                        email: data.email,
                        snackOpened: true,
                        snackType: 'success',
                        snackMessage: 'Login successful!',
                    });
                    sessionStorage.setItem('isAdmin', data.isAdmin);
                    sessionStorage.setItem('userId', data.userId);
                    sessionStorage.setItem('username', data.username);
                    sessionStorage.setItem('token', data.token);
                }
            })
            .catch((err) => {
                console.log(err)
            })
    }

    handleSnackClose() {
        this.setState({snackOpened: !this.state.snackOpened})
    }

    render() {
        return (
            <BrowserRouter>
                <Fragment>
                    <header>
                        <NavBar/>
                    </header>
                    <Switch>
                        {/*<Route path="/" exact component={Home}/>*/}
                        <Route path="/register" render={() =>
                            <DynamicUserForm registerForm={true} loginUser={this.loginUser}/>}
                        />
                        <Route path="/login" render={() =>
                            <DynamicUserForm registerForm={false} loginUser={this.loginUser}/>}
                        />

                    </Switch>

                    <SnackbarWrapper handleSnackClose={this.handleSnackClose} snackOpened={this.state.snackOpened}
                                     snackMessage={this.state.snackMessage} snackType={this.state.snackType}/>
                </Fragment>
            </BrowserRouter>
        );
    }
}

export default App;
