import React, {Component, Fragment} from 'react';
import './App.css';
import NavBar from './components/Navbar/NavBar';
import {BrowserRouter, Route, Switch} from 'react-router-dom';
import DynamicUserForm from "./components/DynamicUserForm/DynamicUserForm";
import {Grid, Snackbar} from "@material-ui/core";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            user: null,
            isAdmin: false,
            isModerator: false,
            snackOpened: false,
            snackMessage: null,
        }
    }

    render() {
        return (
            <BrowserRouter>
                <Fragment>
                    <header>
                        <NavBar/>
                    </header>
                    <Switch>
                        {/*<Route path="/" exact component={Home}/>*/}
                        <Route path="/register" render={() => <DynamicUserForm registerForm={true} />}/>
                        <Route path="/login" render={() => <DynamicUserForm registerForm={false}/>}/>

                    </Switch>

                    <Snackbar
                        anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                        open={this.state.snackOpened}
                        // onClose={this.handleClose}
                        ContentProps={{
                            'aria-describedby': 'message-id',
                        }}
                        message={<span id="message-id">{this.state.snackMessage}</span>}
                    />
                </Fragment>
            </BrowserRouter>
        );
    }
}

export default App;
